package com.A1.RSA;

import java.math.BigInteger;

public class Main {
    public static void main(String[] args) {
        RSA rsa = new RSA();
        //BigInteger P = new BigInteger("");
        //BigInteger Q = new BigInteger("");


        BigInteger P = new BigInteger("105557");
        BigInteger Q = new BigInteger("105683");


        rsa.KeyGeneration(P, Q);
        rsa.secretKey.PrintKeyInfo();
        rsa.publicKey.PrintKeyInfo();


        String message= "HI";
        System.out.println("\nMessage: "+message);

        BigInteger C = rsa.EncryptMessage(rsa.publicKey, message);
        System.out.println("\nEncrypted Message");
        System.out.println(C.toString());

        String m = rsa.DecryptMessage(rsa.secretKey, C);
        System.out.println("\nDecrypted Message");
        System.out.println(m.toString());






    }

}